import React from 'react'
import './App.css';
import { useState } from 'react';
import { useEffect } from 'react';

function App() {
  const [timer, setTimer] = useState(0);

  useEffect(() => {
    const timerId = setInterval(() => {
      setTimer((prevTimer) => prevTimer + 1)
      console.log('timer');
    }, 1000)

    return () => { 
      clearInterval(timerId);
    }
  }, [])

  const handleClearTimer = () => {
    setTimer(0)
  }

  return (
    <div className="App">
      <h1>{timer} секунд</h1>
      <button>Старт</button>
      <button>Пауза</button>
      <button onClick={handleClearTimer}>Сброс</button>
    </div>
  );
}

export default App;
