import React, { useState, useEffect } from 'react';
import MyBtn from './MyBtn';
import Timer from './Timer';

const TimerWrapper = () => {
    const [timer, setTimer] = useState(0)
    const [isRunning, setIsRunning] = useState(false);


    useEffect(() => {
        let timerId;

        if (isRunning) {
            timerId = setInterval(() => {
                setTimer((prevTimer) => {
                    if (prevTimer === 1) {
                        clearInterval(timerId);
                        alert('Timer ended');
                        setIsRunning(false);
                        return 0;
                    }
                    return prevTimer - 1;
                });
            }, 1000);
        }

        return () => {
            clearInterval(timerId);
        };
    }, [isRunning]);

    const handleBtnClick = (seconds) => {
        setTimer(seconds);
        setIsRunning(true);
    };

    return (
        <div>
            <MyBtn onClick={() => handleBtnClick(5)}>5 секунд</MyBtn>
            <MyBtn onClick={() => handleBtnClick(10)}>10 секунд</MyBtn>
            <MyBtn onClick={() => handleBtnClick(15)}>15 секунд</MyBtn>
            <h1>{timer}</h1>
        </div>
    );
};

export default TimerWrapper;
