import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [timer, setTimer] = useState(null);
  const [timeLeft, setTimeLeft] = useState(false)
  const [isRunning, setIsRunning] = useState(false);

  useEffect(() => {
    let timerId;

    if (isRunning) {
      timerId = setInterval(() => {
        setTimer((prevTimer) => {
          if (prevTimer === 1) {
            clearInterval(timerId);
            setTimeLeft(true)
            setIsRunning(false);
            return 0;
          }
          return prevTimer - 1;
        });
      }, 1000);
    }

    return () => {
      clearInterval(timerId);
    };
  }, [isRunning]);

  const handleStart = () => {
    setIsRunning(true);
  };

  const handlePause = () => {
    setIsRunning(false);
  };

  const handleClearTimer = () => {
    setIsRunning(false);
    setTimer(0);
  };

  return (
    <div className="App">
      {timeLeft ?
        <h1>Время вышло</h1>
        :
        <h1>{timer} секунд</h1>
      }
      <button onClick={handleStart}>Старт</button>
      <button onClick={handlePause}>Пауза</button>
      <button onClick={handleClearTimer}>Сброс</button>
      <button onClick={() => {setTimer(5); setIsRunning(true)}}>5 секунд</button>
      <button onClick={() => { setTimer(10); setIsRunning(true) }}>10 секунд</button>
      <button onClick={() => { setTimer(15); setIsRunning(true) }}>15 секунд</button>
    </div>
  );
}

export default App;
