import React, { useState, useEffect } from 'react';
import './App.css';
import MyBtn from './MyBtn';

function App() {
  const [timer, setTimer] = useState(null);
  const [timeLeft, setTimeLeft] = useState(true)
  const [isRunning, setIsRunning] = useState(false);

  useEffect(() => {
    let timerId;

    if (isRunning) {
      timerId = setInterval(() => {
        setTimer((prevTimer) => {
          if (prevTimer === 1) {
            clearInterval(timerId);
            setTimeLeft(true)
            setIsRunning(false);
            return 0;
          }
          return prevTimer - 1;
        });
      }, 1000);
    }

    return () => {
      clearInterval(timerId);
    };
  }, [isRunning]);

  const handleStart = (seconds) => {
    setIsRunning(true);
    setTimeLeft(false);
    setTimer(seconds)
  };

  const handleClearTimer = () => {
    setIsRunning(false);
    setTimeLeft(true);
    setTimer(0);
  };

  return (
    <div className="App">
      {timeLeft ?
        <h1>Время вышло</h1>
        :
        <h1>{timer} секунд</h1>
      }
      <MyBtn time="5" handleStart={handleStart}>5 секунд</MyBtn>
      <MyBtn time="10" handleStart={handleStart}>10 секунд</MyBtn>
      <MyBtn time="15" handleStart={handleStart}>15 секунд</MyBtn>
      <button onClick={handleClearTimer}>Сброс</button>
    </div>
  );
}

export default App;
