import React from 'react'
import './App.css';
import { useState } from 'react';
import { useEffect } from 'react';

function App() {
  const [timer, setTimer] = useState(0);

  useEffect(() => {

    const timerId = setInterval(() => {
      setTimer((prevTimer) => prevTimer++)
    }, 1000)

    return () => { 
      clearInterval(timerId);
    }
  }, [])
  return (
    <div className="App">
      <h1>{timer} секунд</h1>
    </div>
  );
}

export default App;
