import React, { useState, useEffect } from 'react';
import './App.css';
import TimerWrapper from './components/TimerWrapper';

function App() {
  const [timer, setTimer] = useState(0);
  const [isRunning, setIsRunning] = useState(false);

  useEffect(() => {
    let timerId;

    if (isRunning) {
      timerId = setInterval(() => {
        setTimer((prevTimer) => prevTimer - 1);
        console.log('timer');
      }, 1000);
    }

    return () => {
      clearInterval(timerId);
    };
  }, [isRunning]);

  const handleStart = () => {
    setIsRunning(true);
  };

  const handlePause = () => {
    setIsRunning(false);
  };

  const handleClearTimer = () => {
    setIsRunning(false);
    setTimer(0);
  };

  return (
    <div className="App">
      <TimerWrapper />
      {/* <h1>{timer} секунд</h1>
      <button onClick={handleStart}>Старт</button>
      <button onClick={handlePause}>Пауза</button>
      <button onClick={handleClearTimer}>Сброс</button>
      <button onClick={() => {setTimer(15); setIsRunning(true)}}>15 секунд</button> */}
    </div>
  );
}

export default App;
